mapper.select:
select  *  from sys_device_error_rec

    AND start_time >= #{startDate}
    AND end_time <=#{endDate}

ORDER BY start_time DESC
LIMIT #{pageParam.offset},#{pageParam.pageSize}

select  count(*)  from sys_device_error_rec

    AND start_time >= #{startDate}
    AND end_time <=#{endDate}
