mapper.select:
SELECT c.device_id, c.insect_id, c.fmt_date AS `date`, c.fmt_hour AS `hour`, SUM( c.insect_num ) AS num FROM ( SELECT b.*, SUBSTRING_INDEX( b.temp_result, ',', 1 ) AS insect_id, SUBSTRING_INDEX( b.temp_result, ',',- 1 ) AS insect_num FROM ( SELECT a.*, DATE_FORMAT( a.time, '%Y-%m-%d' ) AS fmt_date, DATE_FORMAT( a.time, '%H' ) AS fmt_hour, SUBSTRING_INDEX( SUBSTRING_INDEX( a.result, '#', b.help_topic_id + 1 ), '#',- 1 ) AS temp_result FROM sys_insect_rec AS a LEFT JOIN sys_insect_device AS sid ON sid.id = a.device_id JOIN mysql.help_topic AS b ON b.help_topic_id < ( length( a.result ) - length( REPLACE ( a.result, '#', '' ) ) + 1 )
a.time BETWEEN #{startTime} AND #{endTime}
) AS b ) AS c GROUP BY c.device_id, c.insect_id, c.fmt_date, c.fmt_hour
